  collisions(state) {
    super.collisions(state);
    if ( this.sceneMeshes ) {
      for ( var i=0; i<this.sceneMeshes.length; i++ ) {
        if ( this.sceneMeshes[i].name == 'node4' ) {
          // cave wall
          this.sceneMeshes[i].checkCollisions = state;
          break;
        }
      }
    }
  }
  
  entered(welcome) {
    super.entered(welcome);
    if ( this.hasTouchScreen() ) {
      this.worldManager.remoteLogging = true;
      console.log("Touchscreen detected, assuming mobile device, remote logging enabled");
    } else if (this.hasXR) {
      this.worldManager.remoteLogging = true;
      console.log("XR capable browser, remote logging enabled");
    } else {
      console.log("Looks like PC browser");
      //this.worldManager.remoteLogging = true;
    }
  }
  
  loaded( file, mesh ) {
    // floor editor example (used to create floors in createGround method)
    //this.floor = new FloorRibbon(this);
    //this.floor.showUI();
    
    // make all meshes non-pickable, we react only on floor mesh selection
    if ( this.sceneMeshes ) {
      for ( var i=0; i<this.sceneMeshes.length; i++ ) {
        this.sceneMeshes[i].isPickable = false;
      }
    }
    this.initXR();
  }
  
